import React,{ useState} from "react"



function Adder(){

    const [count,setCount]=useState(0)

    function increase(){
        setCount(count +1)
    }

    function decrease(){
        setCount(count -1)
    }


    return <div class="bottom">
        <h3 class="set t"><span class="badge badge-warning text-dark">{count}</span></h3>
        <div class="set p" ><button onClick={increase} class="btn btn-secondary"><span class="glyphicon glyphicon-plus"></span>+</button></div>
        <div class="set m"><button class="btn btn-success"><span class="glyphicon glyphicon-plus"></span>-</button></div>
        <div class="set d"><button class="btn btn-danger"><span class="glyphicon glyphicon-plus"></span>Delete</button></div>
    </div>
}

export default Adder
