import React,{ useState} from "react"



function Adder(){
    var st="zero"

    const [count, setCount]= useState(st)

    function increase(){
        setCount(count==="zero" ? st=1 : count + 1)
    }

    function decrease(){
        setCount(count>1 ? count -1 : st="zero")
    }


    return <div class="bottom">
        <h3 class="set t"><span class="badge badge-warning text-dark">{count}</span></h3>
        <div class="position-fixed set p" ><button onClick={increase} class="btn btn-secondary"><span class="glyphicon glyphicon-plus"></span>+</button></div>
        <div class="position-fixed set m"><button onClick={decrease} class="btn btn-success"><span class="glyphicon glyphicon-plus"></span>-</button></div>
        <div class="position-fixed set d"><button class="btn btn-danger"><span class="glyphicon glyphicon-plus"></span>Delete</button></div>
    </div>
}

export default Adder
