import React ,{ useState } from "react"
import Adder from "./adder"


function Inter(props){

    let v=1;

    const [items,setItems] =useState([<Adder  key={0}  id={0} Test={test} se={send} />,
        <Adder  key={1}  id={1} Test={test} se={send} />,
        <Adder  key={2}  id={2} Test={test} se={send} />,
        <Adder  key={3}  id={3} Test={test} se={send}/>])

    
        function test(ind){
            setItems(prev => {
                return prev.filter((val,index) =>{
                    return ind!==index
                })
            })
        }
    function send(value,id){
    
        props.tot(value,id)
    }


    function increase(){
        setItems((prev) =>{
        return [...prev, <Adder 
            key={items.length}
            id={items.length}
            Test={test}
            se={send}
        />]
       } )
    }



   

    return <div>
    {items.map((val)=> {
       return [<h4 class="position-absolute no">{v++}</h4>,val]})}

       <div class="position-absolute "><button class="btn btn-light"><i class="bi bi-triangle"></i></button></div>

       <div class="position-absolute set plus"><button onClick={increase} class="btn btn-dark"><span class="glyphicon glyphicon-plus"></span>Add</button></div>
       
    </div>
}
export default Inter
